    if not IsLooped then
        PlaySoundFrontend(-1, Sound, Library, true)
    else
        if not Audio.Id then
            Citizen.CreateThread(function()
                Audio.Id = GetSoundId()

                PlaySoundFrontend(Audio.Id, Sound, Library, true)

                Citizen.Wait(0.01)

                StopSound(Audio.Id)

                ReleaseSoundId(Audio.Id)

                Audio.Id = nil
            end)
        end
    end
