                local name = name:gsub("", "a")
                local name = name:gsub("", "a")
                local name = name:gsub("", "e")
                local name = name:gsub("", "e")
                local name = name:gsub("", "i")
                local name = name:gsub("", "c")
                local name = name:gsub("", "o")
                local name = name:gsub("", "E")
